"""
JWT Validator Component - Extrae mensaje si token válido
"""
from langflow.custom import Component
from langflow.io import MessageTextInput, Output
from langflow.schema import Message
import jwt
import json

# Variable global para compartir session_id entre componentes
FLOW_SESSION_ID = None

class JWTValidatorWithMessage(Component):
    display_name = "JWT Validator + Message"
    description = "Valida JWT y extrae el mensaje del usuario si es válido"
    icon = "shield-check"

    inputs = [
        MessageTextInput(
            name="input_json",
            display_name="📥 Input JSON",
            info="JSON con {token: 'xxx', message: 'yyy', session_id: 'zzz'}",
            required=True,
        ),
        MessageTextInput(
            name="jwt_secret",
            display_name="🔐 JWT Secret Key",
            info="Clave secreta para validar el token",
            value="syssa-secret-key-change-in-production",
            required=True,
        ),
    ]

    outputs = [
        Output(display_name="Valid - User Message", name="valid_output", method="get_valid_output"),
        Output(display_name="Invalid - Error", name="invalid_output", method="get_invalid_output"),
        Output(display_name="Session ID", name="session_output", method="get_session_output"),
    ]

    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)
        self.validation_result = None
        self.user_message = None
        self.user_data = None
        self.session_id = None
        self.error_message = None

    def validate_and_parse(self):
        """Parsea el JSON, valida el token y extrae el mensaje"""
        global FLOW_SESSION_ID

        if self.validation_result is not None:
            return

        try:
            # Parsear el JSON de entrada
            input_data = json.loads(self.input_json)
            token = input_data.get("token")
            message = input_data.get("message")
            user_session_id = input_data.get("session_id")  # Session ID del usuario

            # Obtener el session_id de Langflow (fallback)
            langflow_session_id = self.graph.session_id if hasattr(self, 'graph') and hasattr(self.graph, 'session_id') else None

            # PRIORIDAD: User session > Langflow session > "unknown"
            self.session_id = user_session_id or langflow_session_id or "unknown"

            # Guardar en variable global de Python
            FLOW_SESSION_ID = self.session_id

            # Intentar guardar en state del graph (si está disponible)
            if hasattr(self, 'graph'):
                if hasattr(self.graph, 'set_state'):
                    self.graph.set_state("SECCION_ID", self.session_id)
                # También como atributo directo
                self.graph.SECCION_ID = self.session_id

            self.log(f"🔍 Session ID Usuario: {user_session_id}")
            self.log(f"🔍 Session ID Langflow: {langflow_session_id}")
            self.log(f"✅ SECCION_ID guardado en global: {FLOW_SESSION_ID}")
            self.log(f"✅ SECCION_ID final: {self.session_id}")

            if not token:
                self.validation_result = False
                self.error_message = {
                    "valid": False, 
                    "error": "Token no proporcionado",
                    "session_id": self.session_id
                }
                return

            if not message:
                self.validation_result = False
                self.error_message = {
                    "valid": False, 
                    "error": "Mensaje no proporcionado",
                    "session_id": self.session_id
                }
                return

            # Validar el token
            decoded = jwt.decode(token, self.jwt_secret, algorithms=["HS256"])

            # Token válido
            self.validation_result = True
            self.user_message = message
            self.user_data = {
                "username": decoded.get("sub"),
                "name": decoded.get("name"),
                "empresa": decoded.get("empresa"),
                "session_id": self.session_id
            }

        except json.JSONDecodeError:
            self.validation_result = False
            self.error_message = {
                "valid": False, 
                "error": "JSON inválido",
                "session_id": self.session_id
            }
        except jwt.ExpiredSignatureError:
            self.validation_result = False
            self.error_message = {
                "valid": False, 
                "error": "Token expirado",
                "session_id": self.session_id
            }
        except jwt.InvalidTokenError:
            self.validation_result = False
            self.error_message = {
                "valid": False, 
                "error": "Token inválido",
                "session_id": self.session_id
            }
        except Exception as e:
            self.validation_result = False
            self.error_message = {
                "valid": False, 
                "error": f"Error: {str(e)}",
                "session_id": self.session_id
            }

    def get_valid_output(self) -> Message:
        """Salida cuando el token es válido - retorna solo el mensaje del usuario"""
        self.validate_and_parse()

        if self.validation_result:
            return Message(text=self.user_message)
        else:
            return Message(text="")

    def get_invalid_output(self) -> Message:
        """Salida cuando el token es inválido - retorna error en JSON"""
        self.validate_and_parse()

        if not self.validation_result:
            return Message(text=json.dumps(self.error_message, ensure_ascii=False))
        else:
            return Message(text="")

    def get_session_output(self) -> Message:
        """Salida con el SECCION_ID (prioridad user session)"""
        self.validate_and_parse()
        return Message(text=self.session_id or "")

